int main(){

  cout << "========== FIB to 1000 Digits  ==========" << endl;

  BigInteger fib_a;
  BigInteger fib_b;
  BigInteger fib_c;

  BigInteger count = 2;

  string max_str("");
  BigInteger max = stringToBigInteger(max_str);

  while((fib_c - max) > 0){
    fib_c = fib_a + fib_b;
    fib_a = fib_b;
    fib_b = fib_c;
    count++;
  }
  // found it!
  cout << "FOUND IT!.\nFib " << count << " = " << fib_c << endl;
}
